var SampleCase = Class.create();
SampleCase.prototype = {
    initialize: function() {},

    createOrUpdateSampleCase: function(shortDescription) {

        try {
            var inputs = {};
            inputs['short_description'] = shortDescription; // String 

            // Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
            // sn_fd.FlowAPI.getRunner().action('x_237156_source_0.create_sample_case').inBackground().withInputs(inputs).run();

            // Execute Synchronously: Run in foreground. Code snippet has access to outputs.
            var result = sn_fd.FlowAPI.getRunner().action('x_237156_source_0.create_sample_case').inForeground().withInputs(inputs).run();
            var outputs = result.getOutputs();
            gs.info(this.type + ".createOrUpdateSampleCase: outputs = " + JSON.stringify(outputs));

            // Current action has no outputs defined.		
        } catch (ex) {
            var message = ex.getMessage();
            gs.error(message);
        }

    },

    type: 'SampleCase'
};
